#!/usr/bin/env python3

def findOccurences(inputPattern, inputGenome):
  count = 0
  patternLength = len(inputPattern)
  inputLength = len(inputGenome)
  for i in range(inputLength - patternLength):
    if inputGenome[i:i+patternLength] == inputPattern:
      yield i

def test():
  inputPattern = "ATAT"
  inputGenome = "GATATATGCATATACTT"
  for index in findOccurences(inputPattern, inputGenome):
    print(index, end=" ")
  print()  

def shortQuiz():
  inputPattern = "AGTATGGAG"
